DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `title` varchar(50) NOT NULL,
  `author` varchar(32) NOT NULL,
  `intro` varchar(255) NOT NULL,
  `details` text NOT NULL,
